import React, {
    useState,
    useContext,
    Fragment,
    useEffect,
    memo,
    useCallback,
  } from "react";

  import { GlobalContext } from "../contexts/GlobalContext";

  export const MenuFieldButton = ({item, index}) => {
    const [active, SetActive] = useState(false);
    const { value, setValue } = useContext(GlobalContext);

    const fieldCode = String.fromCharCode(65 + index);

    const handleOnClick = (item) => {
        setValue((prev) => {
          return { ...prev, field: item };
        });
      };


    useEffect(() => {
        console.log(value.gy);
        if (value.gy.fields[index] == value.field) {
            SetActive(!active);
        }
    }, [value.field]);


    if (active)
      return (
        <a className="round-button-work-active" onClick={handleOnClick}>
          {fieldCode}
        </a>
      );
    else
      return (
        <a className="round-button-work-inactive" onClick={handleOnClick}>
          {fieldCode}
        </a>
      );
  };

  export default MenuFieldButton;
