import React, { useState, useContext, Fragment } from "react";

import { GlobalContext } from "../contexts/GlobalContext";
import { ProjectContext } from "../contexts/ProjectContext";

import Map from "./Map";
import church from "../images/church.png";

export const Dropdown = ({ title, items, multiSelect = false }) => {
  const { value, setValue } = useContext(GlobalContext);
  const {viewProject, setViewProject} = useContext(ProjectContext);
  const [open, setOpen] = useState(false);

  const toggle = () => setOpen(!open);

  const handleOnClick = (item) => {
    toggle(!open);
    console.log(item);
    setValue((prev) => {
      return { ...prev, gy: item };
    });
  };

  return (
    <div className="dd-wrapper">
      <div
        tabIndex={0}
        className="dd-header"
        role="button"
        onKeyPress={() => toggle(!open)}
        onClick={() => toggle(!open)}
      >
        <div className="dd-header_title">
          <p className="dd-header_title--bold">
            {title} &nbsp; <span>({value.gy.value})</span>
          </p>
          <img className="church" src={church}></img>
        </div>
      </div>
      {open && (
        <ul className="dd-list">
          {items.map((item) => (
            <li className="dd-list-item" key={item.id}>
              <button
                className="dd-button"
                type="button"
                onClick={() => handleOnClick(item)}
              >
                <span>{item.value}</span>
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Dropdown;
