import React, {
    useState,
    useContext,
    Fragment,
    useEffect,
    memo,
    useCallback,
  } from "react";

  import { GlobalContext } from "../contexts/GlobalContext";

  export const MenuFieldButton = ({item, index}) => {
    const [active, SetActive] = useState(false);
    const { value, setValue } = useContext(GlobalContext);
    let lastfield = value.field;
    const thisField = value.gy.fields[index];


    const handleOnClick = () => {
        setValue((prev) => {
          return { ...prev, field: item };
        });
      };


    useEffect(() => {
        console.log(value.gy);
        if (thisField == value.field) {
            SetActive(true);
        } else SetActive(false);
    }, [value.field == thisField || thisField]);


    if (active)
      return (
        <a className="round-button-work-active" onClick={handleOnClick}>
          {value.field}
        </a>
      );
    else
      return (
        <a className="round-button-work-inactive" onClick={handleOnClick}>
          {value.field}
        </a>
      );
  };

  export default MenuFieldButton;
