import React, { useState, useContext, Fragment, useEffect } from "react";

import { GlobalContext } from "../contexts/GlobalContext";
import { GlobalEdit } from "../contexts/GlobalEdit";
import { UserContext } from "../contexts/UserContext";


import JsonActivities, { cleanComments } from "../utilities/JsonActivities";
import { ProjectContext } from "../contexts/ProjectContext";
import { ProgressContext } from "../contexts/ProgressContext";
import { ProgressBar } from "./ProgressBar";
import { chosenConnection } from "../utilities/Connections";

const ProjectInfo = ({ updateLocalProjectValue }) => {
  const { value, setValue } = useContext(GlobalContext);
  const { viewProject, setViewProject } = useContext(ProjectContext);
  const { temporaryProgress, setTemporaryProgress } = useContext(
    ProgressContext
  );
  const [commentsRendered, setCommentsRendered] = useState([]);
  const [newlyAddedComment, setNewlyAddedComment] = useState(false);
  const [input, setInput] = useState(false);
  const [progressAdded, SetProgressAdded] = useState(false);
  const { authenticated, setAuthenticated } = useContext(UserContext);


  let id = value.gy.id;
  let removed = -1;
  let newComment;


  let startDate;
  if (viewProject)
    startDate = `${viewProject.added.substring(
      8,
      10
    )}.${viewProject.added.substring(5, 7)}.${viewProject.added.substring(
      0,
      4
    )}`;

  const registerInput = (e) => {
    console.log(e.target.value);
    if (e.target.value.length < 4) {
      setInput(false);
    } else setInput(true);
  };

  //learn styledcomponents react
  const showRemoveButton = (commentID, index) => {

    if (authenticated === 2) {
      const prevRemove = `comment${removed}`
      const toRemove = `comment${index}`
      const comment = document.getElementById(toRemove);

      if (comment.className === "remove-comment-button show-remove-comment") {
        comment.className = "remove-comment-button"
        removed = -1;
      } else {
        comment.className = "remove-comment-button show-remove-comment";
        if (removed !== -1 && removed !== index) document.getElementById(prevRemove).className = "remove-comment-button";

        removed = index;
      }

    }
  }

  const removeComment = async (comment, index) => {
    // const projectID = comment.project_id;
    const commentID = comment.comment_id;
    const gyID = comment.gy_id;
    const projectID = comment.project_id;

    console.log(projectID, commentID, gyID);

    try {
      await fetch(`${chosenConnection}/comments/${gyID}/${projectID}/${commentID}`, {
        method: "DELETE"
      })
      const remove = document.getElementById(`comment-li${index}`);
      remove.className = `comment-li${index} hidden`;
      console.log(remove);


    } catch (err) {
      console.error(err.message);
    }
  }

  const renderComments = (arr) => {
    let fetchedComments = arr.map((it, index) => {
      let dateEntry =
        it["added"].substring(8, 10) + "." + it["added"].substring(5, 7);
      return (
        <li className="comment-list-item" id={`comment-li${index}`}>
          <p onClick={() => showRemoveButton(it["comment_id"], index)}>
            - &nbsp; <span className="comment-date">{dateEntry}</span>: &nbsp;
            {it["comment"]}
          </p>
          <div className="remove-comment-button" id={`comment${index}`} onClick={() => removeComment(it, index)}>x</div>
        </li>
      );
    });
    console.log(fetchedComments);
    setCommentsRendered(fetchedComments);
  };


  const getComments = async () => {
    try {
      await fetch(
        `${chosenConnection}/comments/${id}/${viewProject["project_id"]}`
      )
        .then((res) => res.json())
        .then((json) => renderComments(json));
    } catch (err) {
      console.error(err.message);
    }
  };


  const addComment = async () => {
    newComment = document.getElementById("project-textarea").value;
    if (newComment.length < 5) return;
    let ser = viewProject.project_id;
    try {
      const body = {
        id,
        ser,
        newComment,
      };
      await fetch(`${chosenConnection}/comments`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      document.getElementById("project-textarea").value = null;
      setNewlyAddedComment(true);
      setInput(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  const increaseProgress = () => {
    SetProgressAdded(true);


    setTemporaryProgress((prev) => {
      if (temporaryProgress == null) {
        return viewProject.percent_finished + 5;
      } else if (temporaryProgress == 100) {
        return prev;
      } else {
        return prev + 5;
      }
    });
  };

  const regretProgress = () => {
    setTemporaryProgress(null);
    SetProgressAdded(false);
  };

  const submitProgress = () => {
    SetProgressAdded(false);
    try {
      const body = { temporaryProgress };
      console.log(body);
      const response = fetch(
        `${chosenConnection}/projects/${viewProject.gy_id}/${viewProject.project_id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body),
        }
      );

      updateLocalProjectValue(viewProject, temporaryProgress);
      setTemporaryProgress(body["temporaryProgress"]);
    } catch (err) {
      console.log(err.message);
    }
  };

  const progressControls = () => {
    if (viewProject && viewProject.date_completed) {
      let endDate = `${viewProject.added.substring(
        8,
        10
      )}.${viewProject.added.substring(5, 7)}.${viewProject.added.substring(
        0,
        4
      )}`;

      return (
        <>
          <div className="project-complete-text">
            <p>Fullf√∏rt:</p>
            <p>{endDate}</p>
          </div>
        </>
      );
    } else
      return (
        <>
          <div className="progress-input" onClick={increaseProgress}>
            +
          </div>
          <div
            className={`progress-abort-button ${progressAdded ? "show" : ""}`}
            onClick={regretProgress}
          >
            x
          </div>
          <div
            className={`progress-register-button ${
              progressAdded ? "show" : ""
              }`}
            onClick={submitProgress}
          >
            &#10003;
          </div>
        </>
      );
  };

  const progressView = progressControls();

  useEffect(() => {
    console.log(removed);
    regretProgress();
    getComments();
  }, [viewProject]);

  useEffect(() => {
    getComments();
    setNewlyAddedComment(false);
  }, [newlyAddedComment]);

  if (viewProject) {
    return (
      <div className="project-info-container">
        <div className="project-title-container">
          <div className="project-visual-info">
            <div className="project-prio-visual">
              P{viewProject.project_prio}
            </div>
          </div>
          <p>{viewProject.project_title}</p>
        </div>
        <div className="project-start-date">
          <p>Startet: {startDate}</p>
        </div>

        <div className="progress-box">
          <ProgressBar done={viewProject.percent_finished} />
          {progressView}
        </div>

        <div className="project-descr-container">
          <p className="project-description-header">Tilleggsinformasjon</p>
          <p className="project-description">{viewProject.project_descr}</p>
        </div>
        <div className="project-completion-container"></div>
        <div className="comment-list-container">
          <p className="comment-list-header">Kommentarer</p>
          <ul className="comment-list">{commentsRendered}</ul>
          <div className="comment-input-container">
            <textarea
              className="comment-textarea"
              id="project-textarea"
              placeholder=" Skriv en kommentar &#13;&#10;  (minst 4 tegn)"
              onChange={(e) => registerInput(e)}
            ></textarea>
            <div
              className={`add-comment-button ${input ? "show" : ""}`}
              id="submit-comment"
              onClick={addComment}
            >
              +
            </div>
          </div>
        </div>
      </div>
    );
  } else return null;
};

export default ProjectInfo;
