import React, {
    useState,
    useContext,
    Fragment,
    useEffect,
    memo,
    useCallback,
  } from "react";

  import { GlobalContext } from "../contexts/GlobalContext";

  export const MenuFieldButton = ({index}) => {
    const [active, SetActive] = useState(false);
    const { value, setValue } = useContext(GlobalContext);

    const fieldCode = String.fromCharCode(65 + index);



    useEffect(() => {
        if (value.gy.field.charCodeAt(0) - 65 == index) {
            SetActive(!active);
        }
    }, [value.gy.field]);


    if (active)
      return (
        <a className="round-button-work-active" onClick={toggle}>
          {fieldCode}
        </a>
      );
    else
      return (
        <a className="round-button-work-inactive" onClick={toggleInactive}>
          {fieldCode}
        </a>
      );
  };

  export default MenuFieldButton;
